import React, { useEffect, useState } from 'react';
import './index.css';

function App() {
  const logger = require('./logger');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000');
    ws.onmessage = (event) => {
      const msg = JSON.parse(event.data);
      // Only add messages that are not the test message
      if (msg.value !== 'Test message from backend') {
        setMessages(prev => [msg, ...prev]);
      }
    };
    return () => ws.close();
  }, []);

  return (
    <div style={{
      maxWidth: 600,
      margin: '40px auto',
      padding: 24,
      background: '#f9f9f9',
      borderRadius: 12,
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
    }}>
      <h1 style={{ textAlign: 'center', color: '#1976d2' }}>Real-Time Kafka Messages</h1>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {messages.map((msg, idx) => (
          <li key={idx} style={{
            background: '#fff',
            margin: '12px 0',
            padding: '12px 18px',
            borderRadius: 8,
            boxShadow: '0 1px 3px rgba(0,0,0,0.05)'
          }}>
            <strong>{msg.value}</strong>
            <div style={{ fontSize: 12, color: '#888' }}>{msg.timestamp}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
